import React from 'react'
import styles from '../BurgerControl.module.css'

const burgerControl = (props) => {

    return (
        <div className={styles.BurgerControl}>
            <button onClick={ props.change}>  Salad </button>
            <button>  Bacon </button>
        </div>
        
    );
}




export default burgerControl;
