import React, { Component, useState } from 'react';
import styles from './App.module.css';
import Person from '../components/Persons/Person/Person';

class App extends Component {
  state = ({
    persons: [
      { id: 'qwe', name: 'Niv', age: 28 },
      { id: 'asd', name: 'Noa', age: 23 },
      { id: 'zxc', name: 'Nitai', age: 30 }
    ],
    showPersons: false

  }); 


  deletePersonHandler = (indexPerson) => {
    //const persons = this.state.persons;
    const persons = [...this.state.persons]
    persons.splice(indexPerson, 1);
    this.setState({ persons: persons });

  }

  nameChangedHandler = (event, id) => {
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id
    });

    const person = {
      ...this.state.persons[personIndex]
    };
    person.name = event.target.value;

    const persons = [...this.state.persons];
    persons[personIndex] = person;

    this.setState({ persons: persons })
  }

  switchNameHandler = (newName) => {
    //console.log('was Clicked!');
    this.setState({
      persons: [
        { name: newName, age: 28 },
        { name: 'Noa', age: 23 },
        { name: 'Nitai', age: 40 }
      ]
    })
  };

  togglePersonsHandler = () => {
    const temp = this.state.showPersons;
    this.setState({ showPersons: !temp });
  }


  render() {
    let persons = null;
    let btnStyle = styles.button;

    if (this.state.showPersons) {
      persons = (
        <div>
          {this.state.persons.map((person, index) => {
            return <Person
              click={() => this.deletePersonHandler(index)}
              name={person.name}
              age={person.age}
              key={person.id}
              changed={(event) => this.nameChangedHandler(event, person.id)}
            />
          })}
        </div>
      )

    btnStyle= styles.Red;

    }

    const assignedStyles = [];
    if (this.state.persons.length <= 2) {
      assignedStyles.push(styles.red);
    }
    if (this.state.persons.length <= 1) {
      assignedStyles.push(styles.bold);
    }

    return (
      <div className={styles.App}>
        <h1>Hi, I'm a React App</h1>
        <p className={assignedStyles.join(' ')} >This is really working</p>
        <button
          className= {btnStyle}
          onClick={this.togglePersonsHandler}>Toggle Names</button>
        {persons}
      </div>
    );
  }
}





export default App;




