import React, { Component } from 'react';
import Auxlier from '../../hoc/Auxiler';
import Burger from '../../components/Burger/Burger'
import BurgerControl from '../../components/BurgerControl/BurgerControl'



class buildBurger extends Component {
    state = {
        ingredients: {
            Salad: 0,
            Bacon: 0,
            Cheese: 0,
            Meat: 0,
        },

    };

    addIngrdient = ()  => {
        let ingredientsCopy = {...this.state.ingredients}
        ingredientsCopy['Salad'] ++ ;

        console.log(ingredientsCopy.Salad)
        this.setState({ingredients : ingredientsCopy})

    }

    render() {


        return (
            <Auxlier>
               
                <Burger
                    ingredients={this.state.ingredients}
                />
                 <BurgerControl
                    change= {() => this.addIngrdient}
                />

            
            </Auxlier>

        
        );
    }

}

export default buildBurger;






