import React, {useEffect, useRef} from 'react';
import styles from './Cockpit.module.css';


const Cockpit = (props) => {
    const toggleRefBut = useRef(null);
    toggleRefBut.current.click()

    useEffect(() => {
      console.log('[Cockpit.js useEffect');
      //http request...
      setTimeout(() => {
        alert('Saved Data To The Cloud');
      }, 1000);
      
    }, []);


    const assignedStyles = [];
    let btnStyle = '';

    if (props.showPersons){
        btnStyle = styles.Red;
    }

    if (props.persons.length <= 2) {
      assignedStyles.push(styles.red);
    }
    if (props.persons.length <= 1) {
      assignedStyles.push(styles.bold);
    }

    return(
    <div className={styles.Cockpit} >
        <h1 className={styles.title}> {props.title}</h1>
        <p className={assignedStyles.join(' ')} >This is really working</p>
        <button
          ref={toggleRefBut}
          className={btnStyle}
          onClick= {props.clicked}>Toggle Names</button>
    </div>
    )
};

export default Cockpit;
