import React from 'react';
import Button from '../../UI/Button/Button';


const orderSummary = (props) => {

    let listOfIngredients = Object.keys(props.ingredients).map(
        igKey =>
            <li key={igKey}>
                <span style={{ textTransform: 'capitalize' }}> {igKey} </span>  : {props.ingredients[igKey]}
            </li>
    )

    // [Salad : 1  , Bacon: 2] ....

    return (
        <div>
            <h3> Your Order </h3>
            <p> the Amazing Burger With The Following Ingredients: </p>
            <ul>
                {listOfIngredients}
            </ul>
            <p> Continue To ChechOut?</p>

            <Button
                btnType={"Danger"}
                clicked={props.backToOrder}>
                CANCLE  </Button>

            <Button
                btnType={"Success"}
                clicked={props.continueToCheckout}
                > CONTINUE </Button>

        </div>
    );

};

export default orderSummary;
